import React, { useState } from "react";
import { StatusBar } from "expo-status-bar";
import { HStack, NativeBaseProvider, Checkbox } from "native-base";
import {
  StyledContainer,
  InnerContainer,
  PageLogo,
  PageTitle,
  SubTitle,
  StyledFormArea,
  StyledTextInput,
  StyledInputLabel,
  LeftIcon,
  RightIcon,
  StyledButton,
  ButtonText,
  Colors,
  MsgBox,
  SimpleButton,
  SimpleButtonText,
  SignOptionsView,
  TextLink,
  TextLinkContent,
  Line,
  ExtraView,
  ExtraText,
  Banner, BannerLogo, BannerTitle
} from "./../components/styles";

import { View } from "react-native";
import { Formik } from "formik";
import { Octicons, Ionicons } from "@expo/vector-icons";
import KeyboardAvoidingWrapper from "../components/KeyboardAvoidingWrapper";

const { brand, darkLight, tertiary } = Colors;
const Login = ({ navigation }) => {
  const [hidePassword, setHidePassword] = useState(true);
  const [isChecked, setChecked] = useState(false);
  return (
    <NativeBaseProvider>
      <KeyboardAvoidingWrapper>
        <StyledContainer>
          <StatusBar style="dark" />
          <InnerContainer>
            <Banner>
              <BannerLogo
                resizeMode="cover"
                source={require("./../assets/img/page_logo.png")}
              />
              <BannerTitle
                resizeMode="cover"
                source={require("./../assets/img/page_title.png")}
              />
            </Banner>
            <SubTitle style={{marginTop: 30, marginBottom: 25}}>Sign in</SubTitle>
            <Formik
              initialValues={{ email: "", password: "" }}
              onSubmit={(values) => {
                console.log(values);
                navigation.navigate("Homepage");
              }}
            >
              {({ handleChange, handleBlur, handleSubmit, values }) => (
                <StyledFormArea>
                  <MyTextInput
                    label="Email"
                    icon="person"
                    placeholder="grayhatdevs@gmail.com"
                    placeholderTextColor={darkLight}
                    onChangeText={handleChange("email")}
                    onBlur={handleBlur("email")}
                    value={values.email}
                    keyboardType="email-address"
                  />
                  <MyTextInput
                    label="Password"
                    icon="lock"
                    placeholder="* * * * * * *"
                    placeholderTextColor={darkLight}
                    onBlur={handleBlur("password")}
                    value={values.password}
                    onChangeText={handleChange("password")}
                    secureTextEntry={hidePassword}
                    isPassword={true}
                    hidePassword={hidePassword}
                    setHidePassword={setHidePassword}
                  />
                  <MsgBox>...</MsgBox>
                  <StyledButton onPress={handleSubmit}>
                    <ButtonText>Sign in </ButtonText>
                  </StyledButton>
                </StyledFormArea>
              )}
            </Formik>

            <SignOptionsView>
              <HStack w="90%" justifyContent="space-between">
                <Checkbox
                  accessibilityLabel="remember user"
                  isChecked={isChecked}
                  onChange={() => setChecked(!isChecked)}
                >
                  Remember me
                </Checkbox>
                <SimpleButton>
                  <SimpleButtonText link={true}>
                    Forgot Password
                  </SimpleButtonText>
                </SimpleButton>
              </HStack>
            </SignOptionsView>
            <Line style={{marginTop: 100}}></Line>
            <ExtraView>
              <ExtraText>Don't have an account yet? </ExtraText>
              <TextLink onPress={() => navigation.navigate("SignUp")}>
                <TextLinkContent>Sign up</TextLinkContent>
              </TextLink>
            </ExtraView>
          </InnerContainer>
        </StyledContainer>
      </KeyboardAvoidingWrapper>
    </NativeBaseProvider>
  );
};

const MyTextInput = ({
  label,
  icon,
  isPassword,
  hidePassword,
  setHidePassword,
  ...props
}) => {
  return (
    <View>
      <LeftIcon>
        <Octicons name={icon} size={30} color={tertiary} />
      </LeftIcon>
      <StyledInputLabel>{label}</StyledInputLabel>
      <StyledTextInput {...props} />
      {isPassword && (
        <RightIcon onPress={() => setHidePassword(!hidePassword)}>
          <Ionicons
            name={hidePassword ? "md-eye-off" : "md-eye"}
            size={30}
            color={tertiary}
          />
        </RightIcon>
      )}
    </View>
  );
};

export default Login;
