import React, { useState } from "react";
import { Checkbox, NativeBaseProvider } from "native-base";
import {
  Banner,
  IconContainer,
  InnerContainer,
  StyledButton,
  StyledContainer,
  BannerLogo,
  BannerTitle,
  TransparentButton,
  SimpleButtonText,
  ItemText,
  SubTitle,
  PageHeader,
  RightIcon,
  StyledFormArea,
  StyledInputLabel,
  StyledTextInput,
  LeftIcon,
} from "../components/styles";
import {
  SafeAreaView,
  StatusBar,
  TouchableOpacity,
  View,
  Text,
} from "react-native";
import { Formik } from "formik";
import { ButtonText, Line } from "../components/styles";
import { Octicons, FontAwesome5 } from "@expo/vector-icons";
import { Colors, PageTitle } from "../components/styles";

import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator } from "@react-navigation/drawer";
import Login from "./Login";
import KeyboardAvoidingWrapper from "../components/KeyboardAvoidingWrapper";
import { alignContent } from "styled-system";

const { primary, brand, tertiary, darkLight, red } = Colors;

const Agreement = ({navigation}) => {
  const [isChecked, setChecked] = useState(false);
  return (
    <NativeBaseProvider>
      <KeyboardAvoidingWrapper>
        <StyledContainer>
          <StatusBar style="dark" />
          <InnerContainer>
            <Banner>
              <BannerLogo
                resizeMode="cover"
                source={require("./../assets/img/page_logo.png")}
              />
              <BannerTitle
                resizeMode="cover"
                source={require("./../assets/img/page_title.png")}
              />
            </Banner>
            <PageHeader>Agreement</PageHeader>
            <Line></Line>
            <View
              style={{
                flexDirection: "column",
                alignItems: 'center'
              }}
            >
              
              <Text style={{fontSize: 20, textAlign: 'justify'}}>I hereby certifies and agrees to this contract and promises to
                pay the full amount. I hereby agreed to be paid, the balance, to
                be paid on every due date. I also further agrees to pay all
                attorneyâ€™s fees and court costs in the case of litigation.</Text>
              <Checkbox
                accessibilityLabel={"Agree"}
                isChecked={isChecked}
                onChange={() => setChecked(!isChecked)}
                size= 'md'
                marginBottom={2}
              >Yes, I agree.</Checkbox>
            </View>

            <Formik
              initialValues={{
                agree: "",
              }}
              onSubmit={(values) => {
                values = { ...values };
                console.log(values);
              }}
            >
              {({ handleChange, handleBlur, handleSubmit, values }) => (
                <StyledFormArea>
                  <StyledButton
                    onPress={() => navigation.navigate("MyPackages")}
                    style={{ backgroundColor: red }}
                  >
                    <ButtonText>Cancel</ButtonText>
                  </StyledButton>
                  <StyledButton onPress={handleSubmit}>
                    <ButtonText>Proceed</ButtonText>
                  </StyledButton>
                </StyledFormArea>
              )}
            </Formik>
          </InnerContainer>
        </StyledContainer>
      </KeyboardAvoidingWrapper>
    </NativeBaseProvider>
  );
};

const MyTextInput = ({ label, icon, ...props }) => {
  return (
    <View>
      <LeftIcon>
        <Octicons name={icon} size={30} color={tertiary} />
      </LeftIcon>
      <StyledInputLabel>{label}</StyledInputLabel>
      <StyledTextInput {...props} />
    </View>
  );
};

export default Agreement;
