import styled from "styled-components";

import Constants from "expo-constants";
import { justifyContent } from 'styled-system';

const StatusBarHeight = Constants.statusBarHeight;

export const Colors = {
  primary: "#ffffff",
  secondary: "#e5e7eb",
  tertiary: "#1f2937",
  darkLight: "#9ca3af",
  brand: "#66bb6a",
  green: "#10b981",
  red: "#e57373",
  blue: "#1a9eeb",
};

const statusbarHeight = Constants.statusBarHeight;
const { primary, secondary, tertiary, darkLight, brand, green, red, blue } =
  Colors;

export const StyledContainer = styled.View`
  flex: 1;
  padding: 20px;
  padding-top: ${StatusBarHeight + 30}px;
  background-color: ${primary};
`;

export const InnerContainer = styled.View`
  flex: 1;
  width: 100%;
  align-items: center;
`;
export const PageLogo = styled.Image`
  width: 250px;
  height: 200px;
  margin-bottom: 20px;
`;
export const PageTitle = styled.Text`
  font-size: 30px;
  text-align: center;
  font-weight: bold;
  color: ${brand};
  padding: 0px;
`;
export const SubTitle = styled.Text`
  font-size: 20px;
  margin-bottom: 10px;
  margin-top: 10px
  letter-spacing: 1px;
  font-weight: bold;
  color: ${tertiary};
`;

export const StyledFormArea = styled.View`
  width: 100%;
`;

export const StyledTextInput = styled.TextInput`
  background-color: ${secondary};
  padding: 15px;
  padding-left: 55px;
  padding-right: 55px;
  border-radius: 5px;
  font-size: 16px;
  height: 50px;
  margin-vertical: 3px;
  margin-bottom: 10px;
  color: ${tertiary};
`;

export const StyledInputLabel = styled.Text`
  color: ${tertiary};
  font-size: 13px;
  text-align: left;
`;

export const LeftIcon = styled.View`
  left: 15px;
  top: 30px;
  position: absolute;
  z-index: 1;
`;

export const RightIcon = styled.TouchableOpacity`
  right: 15px;
  top: 30px;
  position: absolute;
  z-index: 1;
`;

export const StyledButton = styled.TouchableOpacity`
  padding: 15px;
  background-color: ${brand};
  justify-content: center;
  border-radius: 5px;
  margin-vertical: 5px;
  align-items: center;
  height: 60px;

  ${(props) =>
    props.iconButton &&
    `
    background-color: ${brand};
    flex-direction: row;
    justify-content: flex-start;
    border-radius: 10px;
    height: 90px;
    width: 90%;
  `}
`;

export const ButtonText = styled.Text`
  color: ${primary};
  font-size: 16px;
  font-weight: bold;

  ${(props) =>
    props.iconButton &&
    `
    padding: 20px;
  `}
`;

export const MsgBox = styled.Text`
  font-size: 13px;
  text-align: center;
`;

export const SimpleButton = styled.TouchableOpacity`
  padding: 0px;

  justify-content: center;
  align-items: center;
  right: 0px;
  position: absolute;
  z-index: 1;
`;

export const SimpleButtonText = styled.Text`
  color: ${tertiary};
  font-size: 16px;
  text-align: left;

  ${(props) =>
    props.link == true &&
    `
        color: ${blue}
        font-size: 16px;
    `}
`;

export const SignOptionsView = styled.View`
  height: 50px;
  margin-top: 10px;
  flex-direction: row;
`;

export const ExtraView = styled.View`
  justify-content: center;
  flex-direction: row;
  align-items: center;
  padding: 10px;
`;

export const ExtraText = styled.Text`
  font-size: 15px;
  color: ${tertiary};
`;

export const TextLink = styled.TouchableOpacity`
  justify-content: center;
  align-items: center;
`;

export const TextLinkContent = styled.Text`
  color: ${brand};
  font-size: 15px;
`;

export const Line = styled.View`
  height: 1px;
  width: 100%;
  background-color: ${darkLight};
  margin-vertical: 10px;
`;

export const IconContainer = styled.View`
  height: 60px;
  width: 60px;
  align-items: center;
  justify-content: center;
  border-radius: 100px;
`;

export const Banner = styled.View`
  align-items: center;
  flex-direction: row;
  justify-content: space-evenly;
  margin-top: 10px;
  margin-bottom: 10px;
`;

export const BannerLogo = styled.Image`
  height: 100px;
  width: 100px;
  margin-bottom: 20px;
`;

export const BannerTitle = styled.Image`
  height: 100px;
  margin-bottom: 20px;
`;

export const Hamburger = styled.TouchableOpacity`
  align-items: flex-end;
  margin: 15px;
`;

export const DrawerItemView = styled.View`
  background-color: ${brand};
  flex-direction: row;
  justify-content: flex-start;
  border-radius: 10px;
  height: 30px;
  margin-right: 10px;
`;

export const TransparentButton = styled.TouchableOpacity`
  padding: 30px;
  justify-content: center;
  align-items: center;
  height: 30px;
  border-style: solid;
  border-width: 1px;
  width: 100%;
`;

export const ItemText = styled.Text`
  font-size: 18px;
  text-align: left;
  align-self: flex-start;
`;

export const PageHeader = styled.Text`
  font-size: 20px;
  margin-top: 10px
  letter-spacing: 1px;
  font-weight: bold;
  color: ${tertiary};
  align-self: flex-start;
`;

export const Card = styled.View`
  background-color: ${brand};
  margin-bottom: 10px;
  width: 100%;
  border-radius: 5px;
`;


export const CardText = styled.Text`
  font-size: 20px;
  color: ${primary};
  font-weight: bold;
  text-align: center;
  `;

  export const CardButton = styled.TouchableOpacity`
  background-color: ${brand};
  width: 100%;
  border-radius: 5px;
  flex-direction: row;
  justify-content: space-between;
  padding: 10px;
  `;
